                                                                                                                                                                                                                                                                                                                         			if (dis != null) {
				JSONObject disJson = JSONObject.parseObject(dis);
				if (!disJson.getBooleanValue("success")) {
					throw new EminException(disJson.getString("code"));
				}
				mv.addObject("dis", disJson.get("result"));
			}
